import React, { Component } from 'react';
import { StyleSheet, Text, View, Image, Dimensions, StatusBar } from 'react-native';
import * as Animatable from 'react-native-animatable';
import { Header } from 'react-navigation';

import HeaderImageScrollView, { TriggeringView } from 'react-native-image-header-scroll-view';
import tvShowContent from './Content.js';


const MIN_HEIGHT = Header.HEIGHT;
const MAX_HEIGHT = 200;

const styles = StyleSheet.create({
  image: {
    height: 200,
    width: "100%",
    alignSelf: 'stretch',
    resizeMode: 'cover',
  },
  title: {
    fontSize: 5,
  },
  name: {
    fontWeight: 'bold',
  },
  section: {

    borderBottomWidth: 1,
    borderBottomColor: '#cccccc',
    backgroundColor: 'white',
    height:"100%",
    flex:1,
  },
  sectionTitle: {
    fontSize: 15,
    fontWeight: 'bold',
  },
  sectionContent: {
    fontSize: 15,
    textAlign: 'justify',

  },
  keywords: {
    flexDirection: 'row',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    flexWrap: 'wrap',
  },
  keywordContainer: {
    backgroundColor: '#999999',
    borderRadius: 10,
    margin: 10,
    padding: 10,
  },
  keyword: {
    fontSize: 16,
    color: 'white',
  },
  titleContainer: {
    alignSelf: 'stretch',
    height:"100%",
    justifyContent: 'center',
    alignItems: 'center',
  },
  imageTitle: {
    color: 'white',
    backgroundColor: 'transparent',
    fontSize: 20,
  },
  navTitleView: {
    height: MIN_HEIGHT,
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: 16,
    height:"100%",
    opacity: 0,
  },
  navTitle: {
    color: 'white',
    fontSize: 18,
    flex:1,
    height:"100%",
    backgroundColor: 'transparent',
  },
  sectionLarge: {
    height: "100%",
  },
});

class Anim extends Component {
  constructor() {
    super();
    this.state = { 
        showNavTitle: false,
        offset: 0
         };
  }


  render() {
    let offset=0;

    return (
      <View style={{ flex: 1,heigh:"100%" }}>
        <HeaderImageScrollView
        maxHeight={MAX_HEIGHT}
        minHeight={MIN_HEIGHT}
          maxOverlayOpacity={0.6}
          minOverlayOpacity={0.3}
          onScroll={(event)=>{
            var currentOffset = event.nativeEvent.contentOffset.y;
            var direction = currentOffset > offset ? 'down' : 'up';
            offset=currentOffset
            console.log(direction);

          }}
          fadeOutForeground
         renderHeader={() => <Image source={tvShowContent.image} style={styles.image} />}
          renderFixedForeground={() => (
            <Animatable.View
              style={styles.navTitleView}
              ref={navTitleView => {
                this.navTitleView = navTitleView;
              }}
            >
              <Text style={styles.navTitle}>
                {tvShowContent.title}, ({tvShowContent.year})
              </Text>
            </Animatable.View>
          )}
          renderForeground={() => (
            <View style={styles.titleContainer}>
              <Text style={styles.imageTitle}>{tvShowContent.title}</Text>
            </View>
          )}
        >
          <TriggeringView
            style={styles.section}
            onHide={() => this.navTitleView.fadeInUp(200)}
            onDisplay={() => this.navTitleView.fadeOut(100)}

          >
            <Text style={styles.title}>
              <Text style={styles.name}>{tvShowContent.title}</Text>, ({tvShowContent.year})
            </Text>
          </TriggeringView>

          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Overview</Text>
            <Text style={styles.sectionContent}>{tvShowContent.overview}</Text>
          </View>
          <View style={[styles.section, styles.sectionLarge]}>
            <Text style={styles.sectionTitle}>Keywords</Text>
            <View style={styles.keywords}>
              {tvShowContent.keywords.map(keyword => (
                <View style={styles.keywordContainer} key={keyword}>
                  <Text style={styles.keyword}>{keyword}</Text>
                </View>
              ))}
            </View>
          </View>
        </HeaderImageScrollView>
      </View>
    );
  }
}

export default Anim;
